mapper.select:
SELECT 

FROM marry_dict a

WHERE a.id = #{id}

SELECT

FROM marry_dict a
WHERE a.id = #{parentId}

SELECT 

FROM marry_dict a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.parent_id = #{parentId}


		AND a.category = #{category}


		AND label LIKE CONCAT('%', #{label}, '%')




		ORDER BY ${page.orderBy}


		ORDER BY a.sort DESC

SELECT 

FROM marry_dict a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE marry_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
